import{_ as e,o as a,c as o,Q as t}from"./chunks/framework.98c15f2e.js";const p=JSON.parse('{"title":"使用卷积神经网络（CNN）识别手写数字","description":"","frontmatter":{"title":"使用卷积神经网络（CNN）识别手写数字","author":"周一","date":"2023-08-05","categories":["人工智能"],"tags":["机器学习"],"sidebar":"auto"},"headers":[{"level":1,"title":"一、使用卷积神经网络识别手写数字任务简介","slug":"一、使用卷积神经网络识别手写数字任务简介","link":"#一、使用卷积神经网络识别手写数字任务简介","children":[{"level":2,"title":"1、任务简介","slug":"_1、任务简介","link":"#_1、任务简介","children":[]},{"level":2,"title":"2、操作步骤","slug":"_2、操作步骤","link":"#_2、操作步骤","children":[]}]}],"relativePath":"column/MachineLearning/Tensorflow/09_CNN.md","filePath":"column/MachineLearning/Tensorflow/09_CNN.md","lastUpdated":1720881464000}'),l={name:"column/MachineLearning/Tensorflow/09_CNN.md"},n=t('<h1 id="一、使用卷积神经网络识别手写数字任务简介" tabindex="-1">一、使用卷积神经网络识别手写数字任务简介 <a class="header-anchor" href="#一、使用卷积神经网络识别手写数字任务简介" aria-label="Permalink to &quot;一、使用卷积神经网络识别手写数字任务简介&quot;">​</a></h1><h2 id="_1、任务简介" tabindex="-1">1、任务简介 <a class="header-anchor" href="#_1、任务简介" aria-label="Permalink to &quot;1、任务简介&quot;">​</a></h2><ul><li>什么是识别手写数字？比如说你在纸上写了一个数字，拍了一张照片传到我们的模型里，然后我们的模型就可以判断你写的这个数字是 0-9 的哪个数字。</li><li>这项任务看似简单，其实非常非常的困难，我们人去看图片识别数字是一件非常简单的事情，这个小朋友都可以做到。</li><li>但是对于计算机来说，处理这种<strong>非结构化的数据</strong>，也就是图片是非常困难的，比如说我们写的数字稍微偏一点儿、歪一点儿、长一点儿、短一点儿，对计算机来说都是完全不一样的信息。且我们的图片最后会变成<strong>一个个像素值</strong>传还给计算机，对计算机来说，识别出来的内容随时都有可能是不一样的数字。</li><li>那么计算机它如何处理这种千变万化的状态呢？这就要用到卷积神经网络了。</li></ul><h2 id="_2、操作步骤" tabindex="-1">2、操作步骤 <a class="header-anchor" href="#_2、操作步骤" aria-label="Permalink to &quot;2、操作步骤&quot;">​</a></h2><ul><li>加载 <code>MNIST</code> 数据集 —— 第一步还是加载数据集，这次我们要加载的是 <code>MNIST</code> 数据集，这个数据集其实就是<strong>一张巨大的雪碧图</strong>，在这个雪碧图里面将会有非常非常多的手写数字。</li><li>构建卷积神经网络并训练 —— 将会介绍卷积神经网络它都包含哪些层，每个层大致的原理是什么样的，以及功能是什么样的。</li><li>使用<code>Canvas</code>绘制数字并预测 —— 用鼠标在<code>canvas</code>画板上写数字，之后把<code>canvas</code>的数据转换成图片，然后把图片转化为<code>Tensor</code>，最后会把这个转化后的 <code>Tensor</code> 输入到模型里面进行预测。</li></ul><p>更详细内容请点击：<a href="https://pzfqk98jn1.feishu.cn/wiki/J1qvw2LQHiEqvLk2rXkca5O0n8d?from=from_copylink" target="_blank" rel="noreferrer">https://pzfqk98jn1.feishu.cn/wiki/J1qvw2LQHiEqvLk2rXkca5O0n8d?from=from_copylink</a></p>',6),i=[n];function r(c,s,d,_,h,u){return a(),o("div",null,i)}const k=e(l,[["render",r]]);export{p as __pageData,k as default};
