import{_ as s,o as a,c as n,Q as t}from"./chunks/framework.98c15f2e.js";const _=JSON.parse('{"title":"基于PHPEnv的本地环境搭建—PHP第一个项目：HelloWorld（从安装到运行）","description":"","frontmatter":{"title":"基于PHPEnv的本地环境搭建—PHP第一个项目：HelloWorld（从安装到运行）","author":"周一","date":"2021-11-17","categories":["其他语言"],"tags":["PHP"],"sidebar":"auto"},"headers":[],"relativePath":"column/OtherLang/PHP/001.md","filePath":"column/OtherLang/PHP/001.md","lastUpdated":1685151998000}'),p={name:"column/OtherLang/PHP/001.md"},o=t(`<p><strong>1、安装软件</strong> 编程工具：Notepad++ 运行环境：phpEnv</p><p><strong>2、用 phpEnv 建立本地运行环境</strong> （<strong>1</strong>）官方下载<a href="https://www.phpenv.cn/" target="_blank" rel="noreferrer">phpEnv</a>，运行 phpEnv，启动服务。服务启动之后，apache 和 mysql 变绿。如下图所示：</p><p>（<strong>2</strong>）建立<strong>站点目录</strong>，例如我的为“E:\\workspace\\test”。</p><p>（<strong>3</strong>）在<strong>站点管理</strong>中，添加站点指向该目录，并设定一个域名（任意虚拟一个），例如我的为“test.local”，如下图所示：</p><p><img src="https://mondaylab-1309616765.cos.ap-shanghai.myqcloud.com/images/202305270902049.png" alt="在这里插入图片描述"></p><p>（<strong>4</strong>）编辑“C:\\Windows\\System32\\drivers\\etc\\hosts”文件（可以用 Notepad++编辑），将站点的域名解析指向本机，在最末端加上“127.0.0.1 test.local”，如下图所示：</p><p><img src="https://mondaylab-1309616765.cos.ap-shanghai.myqcloud.com/images/202305270902019.png" alt="在这里插入图片描述"></p><p><strong>注意</strong>：Hosts 文件修改之后，需要将浏览器重开才会生效。</p><p><strong>3、编写 Hello World 测试环境是否建立成功</strong> 在“E:\\workspace\\test”下建立一个新的文件，命名为 index.php。打开文件，编写以下代码：</p><div class="language-php vp-adaptive-theme line-numbers-mode"><button title="Copy Code" class="copy"></button><span class="lang">php</span><pre class="shiki github-dark vp-code-dark"><code><span class="line"><span style="color:#F97583;">&lt;?</span><span style="color:#79B8FF;">php</span></span>
<span class="line"><span style="color:#79B8FF;">echo</span><span style="color:#E1E4E8;"> </span><span style="color:#9ECBFF;">&quot;Hello World！&quot;</span><span style="color:#E1E4E8;">;</span></span>
<span class="line"><span style="color:#F97583;">?&gt;</span></span></code></pre><pre class="shiki github-light vp-code-light"><code><span class="line"><span style="color:#D73A49;">&lt;?</span><span style="color:#005CC5;">php</span></span>
<span class="line"><span style="color:#005CC5;">echo</span><span style="color:#24292E;"> </span><span style="color:#032F62;">&quot;Hello World！&quot;</span><span style="color:#24292E;">;</span></span>
<span class="line"><span style="color:#D73A49;">?&gt;</span></span></code></pre><div class="line-numbers-wrapper" aria-hidden="true"><span class="line-number">1</span><br><span class="line-number">2</span><br><span class="line-number">3</span><br></div></div><p>保存代码并在浏览器中，访问站点域名，例如我的是“<a href="http://test.local" target="_blank" rel="noreferrer">http://test.local</a>”，演示效果如下：</p><p><img src="https://mondaylab-1309616765.cos.ap-shanghai.myqcloud.com/images/202305270902752.jpg" alt="在这里插入图片描述"></p>`,12),e=[o];function l(r,c,i,d,g,h){return a(),n("div",null,e)}const u=s(p,[["render",l]]);export{_ as __pageData,u as default};
